let icon = document.querySelector('.hamburger');

let animation = bodymovin.loadAnimation({
    container: icon,
    renderer: 'svg',
    loop: false,
    autoplay: false,
    animationData: data
});
animation.setSpeed(2);


let direction = 1;
icon.addEventListener('click', (e) => {
  animation.setDirection(direction);
  animation.play();
  if(direction >0){
    document.querySelector('.overlay-menu').style.opacity = 1;
  } else {
    document.querySelector('.overlay-menu').style.opacity = 0;
  }
  
  direction = -direction;
});